        private void simpleButton6_Click(object sender, EventArgs e)
        {
            int puan = (Convert.ToInt32(numericUpDown1.Value)+Convert.ToInt32(numericUpDown2.Value)+Convert.ToInt32(numericUpDown3.Value)); 
            SqlCommand komut = new SqlCommand("Update Task set GorevPuan=@p1, GorevPuanOnay=@p2, GorevYapildi=@p3 where GorevId=@p4", baglan.baglanti());
            komut.Parameters.AddWithValue("@p1", (puan/3).ToString());
            komut.Parameters.AddWithValue("@p2", "Puanlandırıldı");
            komut.Parameters.AddWithValue("@p3", "Görev Yapıldı");
            komut.Parameters.AddWithValue("@p4", gorevId);
        
            komut.ExecuteNonQuery();
            baglan.baglanti().Close();
            labelControl11.Text = (puan/3).ToString();
        }

