        private void simpleButton3_Click(object sender, EventArgs e)
        {
            SqlCommand komut = new SqlCommand("INSERT INTO Task (IsYeriId, OgrenciId, OgrenIsim, OgrenciSoyisim, GorevKonu, GorevIcerik) VALUES(@p1,@p2,@p3,@p4,@p5,@p6)", baglan.baglanti());
