            komut.Parameters.AddWithValue("@p3", isim);
            komut.Parameters.AddWithValue("@p4", soyisim);
            komut.Parameters.AddWithValue("@p5", comboBox1.Text.ToString());
            komut.Parameters.AddWithValue("@p6", textBox1.Text.ToString());
